to.from:
    perl -I/usr/lib/libDrakX -Mkeyboard -le '{
        my $dir = "bkeymaps";
	mkdir($dir);
        foreach (keyboard::_keyboards()) {
          my $kmap = keyboard::keyboard2kmap($_);
            if (system("loadkeys -b $kmap > $dir/$kmap.bkmap") != 0) {
              warn "unknown kmap $kmap\n";
              unlink "$dir/$kmap.bkmap";
            } else {
              print "$kmap.bkmap";
            }
          }
	}'
